#include <assert.h>
#include <vector>
#include <algorithm>
#include <iostream>
#include <cstring>

using namespace std;


class Solution {
  private:
    bool matchStar(const char *s, const char *p) {
      while (*p == '*')
        p++;
      if (*p == '\0')
        return true;
      if (*p != '?') {
        while (*s && *s != *p)
          s++;
      }
      return isMatch(s, p) || (*s && matchStar(s+1, p));
    }

  public:
    bool isMatch(const char *s, const char *p) {
      vector<char> pp;
      pp.push_back(*p);
      for (p++; *p; p++) {
        if (*p != '*' || *(p-1) != '*')
          pp.push_back(*p);
      }

      int len = pp.size();
      int wid = strlen(s);

      if (len == 0)
        return *s == '\0';

      if (*s == '\0')
        return len == 1 && pp[0] == '*';
      
      vector<bool>  dp(wid+2, false);
      dp[0] = dp[wid+1] = true;
      int last = -1;

      for (int i = len-1; i >= 0; i--) {
        vector<bool> tmp(wid+2, false);

        if (!dp[0])
          return false;
        if (pp[i] == '*') {
          for (int j = 0; j < dp.size() - 1; j++)
            tmp[j] = true;
          tmp.back() = dp.back();
          last = 0;
        } else{
          for (int j = last; j < wid ; j++) {
            if (j+2 != wid+1 && !dp[j+2])
              break;
            if (pp[i] == '?' || pp[i] == s[j]) {
              tmp[j+1] = true;
              tmp[0] = true;
            }
          }
        }

        dp.swap(tmp);
      }

      return dp[1];
    }
};


int
main()
{
  Solution solution;
//  assert(!solution.isMatch("aa", "a"));
  assert(solution.isMatch("aa", "aa"));
  assert(!solution.isMatch("aaa", "aa"));
  assert(solution.isMatch("aa", "*"));
  assert(solution.isMatch("aa", "a*"));
  assert(solution.isMatch("ab", "?*"));
  assert(!solution.isMatch("aab", "c*a*b"));
  assert(solution.isMatch("ho", "**ho"));
  assert(solution.isMatch("", "**"));
}
